import React, {useReducer} from 'react';
import './App.css';

const intialState = 0;
const reducer = (state, action)=>{
  switch(action) {
    case 'increment' :
      return state + 1;
    case 'decrement' :
      return state-1;
    case 'reset':
      return intialState;
    default :
      return state;

  }

}

function App() {
  const [count, dispatch] = useReducer(reducer, intialState);
  return (
    <div className="App">
      <h1>{count}</h1>
      <button onClick={()=>dispatch('increment')}>Increment</button>
      <button onClick={()=>dispatch('decrement')}>Decrement</button>
      <button >Reset</button>
    </div>
  );
}

export default App;
