import React, {useEffect, useState} from 'react'
import Timer from './timer';


const App = () => {
  let array = [];


  const [arr, setArr] = useState([]);
  const handleTimer = () => {
    setArr([...arr, 0]);
  };


  return (
    <div className='App'>
      <button onClick={handleTimer}>Add Timer</button>
      {
        arr.map((ele, idx)=>{
          // (!array.includes(idx)) ?
          return (
            <div>
              <Timer/>
              <button onClick={()=>{arr.push(idx)}}>Del</button>
            </div>
          )
            // :null
        })
      }

    </div>
  )

}


export default App;
