import { createContext, useState } from "react";
const intialUser = [
  {
    name: "Bittu Mondal",
    city: "Kolkata",
    pincode: "713101",
    country: "INDIA",
  },
  {
    name: "Sonam Murakar",
    city: "Kolkata",
    pincode: "713101",
    country: "INDIA",
  },
  {
    name: "Bittu",
    city: "Kolkata",
    pincode: "713101",
    country: "INDIA",
  },
  {
    name: "Bittu",
    city: "Kolkata",
    pincode: "713101",
    country: "INDIA",
  },
];
export const globalContext = createContext();
export default function Context({children}){

    const [user, setUser] = useState(intialUser);
    return(
        <globalContext.Provider value={{user, setUser}}>
            {children}
        </globalContext.Provider>
    )
}
