import React, { useEffect, useState } from 'react'

let k = null;

export default function Child() {

    const [cnt, setCount] = useState(0);
    const [flag, setFlag] = useState(true);
    
    useEffect(()=>{
        if(flag){
            k = setInterval(()=>{
                setCount(p=>p=p+1);
            })
        }

        return ()=>clearInterval(k);
    }, [flag])

  return (
    <div>
        
        <h1>{cnt}</h1>
        <button onClick={()=>setFlag(true)}></button>
    </div>
  )
}
