import React, { useState } from 'react'
import './App.css'
import FillTheBlank from './FillTheBlank';
import MultipleChoise from './MultipleChoise';
import TrueFalse from './TrueFalse';


export default function App() {
  const [questions, setQuestions] = useState([
    {
      question: "What is the nation animal?",
      option: ["lion", "tiger", "cheeta", "elephant"],
      answer: "",
      correct: "tiger",
    },
    {
      question: "Earth is round ?",
      option: ["true", "false"],
      answer: "",
      correct:"false",
    },
    {
      question: "the value of pi is ",
      answer:"",
      correct:"3.14",
    }
  ]);
  const [page, setPage] = useState(1);
  
  return (
    <div className='App'>
      <div className='Header'>
        {
          [...Array(5)].map((ele,idx)=>(
            <div key={idx} className='Header-circle' style={{}} onClick={()=>setPage(idx+1)}><h3>{idx+1}</h3></div>
          ))
        }
      </div>
      
      {page===1 && <MultipleChoise questions={questions} setQuestions={setQuestions} setPage={setPage} page={page}/> }
      {page===2 && <TrueFalse questions={questions} setQuestions={setQuestions} setPage={setPage} page={page}/>}
      {page===3 && <FillTheBlank questions={questions} setQuestions={setQuestions} setPage={setPage} page={page}/>}
    </div>
  );
}
