import React, { useState } from "react";
import "./styles.css";

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [isAccountOpen, setIsAccountOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleSearchToggle = () => {
    setIsSearchOpen(!isSearchOpen);
  };

  const handleAccountToggle = () => {
    setIsAccountOpen(!isAccountOpen);
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <i className="fa fa-diamond" aria-hidden="true" />
      </div>
      <div className={`navbar-search ${isSearchOpen ? "open" : ""}`}>
        <input type="text" placeholder="Search" />
        <button onClick={handleSearchToggle}>
          <i className="fa fa-search" aria-hidden="true" />
        </button>
      </div>
      <div className={`navbar-account ${isAccountOpen ? "open" : ""}`}>
        <div className="dropdown" onClick={handleAccountToggle}>
          <div className="dropdown-toggle">
            <i className="fa fa-user" aria-hidden="true" />
          </div>
          <div className={`dropdown-menu ${isOpen ? "show" : ""}`}>
            <a href="#">Profile</a>
            <a href="#">Settings</a>
            <a href="#">Settings</a>
            <a href="#">Settings</a>
            <a href="#">Settings</a>
            <a href="#">Settings</a>
            <a href="#">Logout</a>
            <hr />
            <a href="#">Login</a>
          </div>
        </div>
      </div>
      <div className={`menu-toggle ${isOpen ? "open" : ""}`} onClick={handleToggle}>
        <i className="fa fa-bars" aria-hidden="true" />
      </div>
    </nav>
  );
}

export default Navbar;
