const App = () => {
  const [items, setItems] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    let temp = data.slice((page - 1) * 10, (page - 1) * 10 + 10);
    setItems([...temp]);
  }, []);

  function infiniteScroll(){
    let scrollHeight = document.documentElement.scrollHeight;
    let windowHeight = window.innerHeight;
    let scrollTop = document.documentElement.scrollTop;
    if(scrollHeight-windowHeight<=Math.floor(scrollTop)) {
      setLoading(true);
      setTimeout(()=>{
        setPage(pre=>pre+1);
        let temp = data.slice((page - 1) * 10, (page - 1) * 10 + 10);
        setItems(pre=>pre=[...items,...temp]);
        setLoading(false);
      },1000)
    }
  }
  useEffect(()=>{
    window.addEventListener("scroll", infiniteScroll);
  },[page])

  console.log(page);
  console.log(items);
  console.log(scrollHeight,);
  //console.log(data);
  return (
    <div className="App">
      {items.map((ele, idx) => (
        <div style={{ border: "1px solid black" }} key={idx + idx}>
          <h1>{idx}</h1>
          <h3>{ele.name}</h3>
          <h4>{ele.city}</h4>
          <h5>{ele.pincode}</h5>
          <h5>{ele.country}</h5>
        </div>
      ))}
      {loading && <div><h1>Loading...</h1></div>}
    </div>
  );
};

export default App;
