import React, {useState, useContext, useNavigate} from 'react'
import {globalContext} from './Context';

const userInfoStyle = {
    height: '100vh',
    border: "1px solid red",
    display: "flex",
    alignItems: "center",
    flexDirection: "column",
}

const userInfoIndividualDivStyle = {
    margin: "20px",
}

const navigate = useNavigate();
export default function UserInfo() {
    const [userInfo, setUserInfo] = useState({
        name:"",
        city:"",
        pincode:"",
        country:"",
    });
    const {user, setUser} = useContext(globalContext);
    return (
        <div style={userInfoStyle}>
        <div style = {userInfoIndividualDivStyle}>
            Name: <input type="text" onChange={(e)=>setUserInfo({...userInfo, name:e.target.value})}/>
        </div>
        <div style = {userInfoIndividualDivStyle}>
            City: <input type="text" onChange={(e)=>setUserInfo({...userInfo, city:e.target.value})}/>
        </div>
        <div style = {userInfoIndividualDivStyle}>
            Pincode: <input type="text" onChange={(e)=>setUserInfo({...userInfo, pincode:e.target.value})}/>
        </div>
        <div style = {userInfoIndividualDivStyle}>
            Country: <input type="text" onChange={(e)=>setUserInfo({...userInfo, country:e.target.value})}/>
        </div>
        <div><button onClick={()=>{setUser([...user, userInfo])}}>Add</button></div>
        </div>
    );
}
