      city: "kolkata",
    },
  ],
};

//const intialState = ["dhjfhdjfhjdh", "dhjfhdjfhjdh","dhjfhdjfhjdh","dhjfhdjfhjdh","dhjfhdjfhjdh","dhjfhdjfhjdh","dhjfhdjfhjdh","dhjfhdjfhjdh","dhjfhdjfhjdh","dhjfhdjfhjdh","dhjfhdjfhjdh"];
const actionReducer = (state = intialState, action)=>{
    switch (action.type) {
      case "ADD_TODO":
        return {
          ...state,
          todoList: [...state.todoList, action.payload],
        };
      case "DEL_TODO":
        let newTodo = [...state.todoList];
        newTodo.splice(action.value, 1);
        return {
            ...state,
            todoList: []
        }
      default:
        return state;
    }
}

export default actionReducer;
