import React, { useContext } from 'react'
import { globalContext } from './Context';

const mainStyle = {

  maxWidth: "100%",
  border: "5px soild red",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  flexWrap: "wrap",
  gap: "2.5rem",
};

const mainIndividualDiv = {
  height: "auto",
  width: "300px",
  border: "1px soild red",
  boxSizing: 'border-box',
  backgroundColor: "grey",
  alignItems: "center",
  margin: "0 auto",
}

export default function Main() {
  const {user, setUser} = useContext(globalContext);
  console.log(user);
  return (
    <div style={mainStyle}>
      {user.map((ele, idx) => (
        <div style={mainIndividualDiv} key={ele.name + idx}>
          <ul style={{ listStyle: "none" }}>
            <li>{ele.name}</li>
            <li>{ele.city}</li>
            <li>{ele.pincode}</li>
            <li>{ele.country}</li>
          </ul>
          <div style={{display:"flex", justifyContent:"space-evenly"}}>
            <button>Edit</button>
            <button onClick={()=>{setUser([...user])}}>Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
}
