import React, { useState } from 'react'
import './App.css'
import FillTheBlank from './FillTheBlank';
import MultipleChoise from './MultipleChoise';
import TrueFalse from './TrueFalse';


export default function App() {
  
  return (
    <div className='App'>
      <div className='Header'>
        {
          [...Array(3)].map((ele,idx)=>(
            <div key={idx} className='Header-circle' style={{backgroundColor:questions[idx].answer?"red":"gray"}} onClick={()=>setPage(idx+1)}><h3>{idx+1}</h3></div>
          ))
        }
      </div>
      
      {page===1 && <MultipleChoise questions={questions} setQuestions={setQuestions} setPage={setPage} page={page}/> }
      {page===2 && <TrueFalse questions={questions} setQuestions={setQuestions} setPage={setPage} page={page}/>}
      {page===3 && <FillTheBlank questions={questions} setQuestions={setQuestions} setPage={setPage} page={page}/>}
    </div>
  );
}
