import React, {useState} from 'react'
import "./App.css";




export default function App() {
  const [counter, setCounter] = useState(2);
  const handleCount = () => {
    setCounter(
      (counter)=>{
        counter = counter===4 ? 2 : 4;
      }
    )
  };
  return (
    <div className='App'>
      <h1 onClick={handleCount}>{counter}</h1>
    </div>
  );
}
