const intialState = {
  todoList: [
    {
      name: "Bittu",
      email: "bittu@gmail.com",
      mobile: "8101593568",
      city: "kolkata",
    },
    {
      name: "Vikah",
      email: "bittu@gmail.com",
      mobile: "8101593568",
      city: "kolkata",
    },
    {
      name: "Sonam",
      email: "bittu@gmail.com",
      mobile: "8101593568",
      city: "kolkata",
    },
    {
      name: "Nikita",
      email: "bittu@gmail.com",
      mobile: "8101593568",
      city: "kolkata",
    },
    {
      name: "Aswini",
      email: "bittu@gmail.com",
      mobile: "8101593568",
      city: "kolkata",
    },
    {
      name: "Depesh",
      email: "bittu@gmail.com",
      mobile: "8101593568",
      city: "kolkata",
    },
  ],
};


const actionReducer = (state = intialState, action)=>{
    switch(action.type){
        case "ADD_TODO": 
            return {
                ...state,
                todoList: [...state.todoList, action.value],
            }
        default :
            return {
                state
            }
    }
}

export default actionReducer;
